import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap, ZoomControl } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function LocationMarker() {
  const [position, setPosition] = useState(null);
  const map = useMap();

  useEffect(() => {
    map.locate().on("locationfound", function (e) {
      setPosition(e.latlng);
      map.flyTo(e.latlng, map.getZoom());
    });
  }, [map]);

  return position === null ? null : (
    <Marker position={position}>
      <Popup>
        <div className="current-location-popup">
          <h3>Ihr Standort</h3>
          <p>Lat: {position.lat.toFixed(4)}</p>
          <p>Lng: {position.lng.toFixed(4)}</p>
        </div>
      </Popup>
    </Marker>
  );
}

function MapControls() {
  const map = useMap();

  useEffect(() => {
    // Add custom controls here if needed
    map.zoomControl.setPosition('topright');
  }, [map]);

  return null;
}

const Map = ({ onLocationSelect }) => {
  const [selectedLocation, setSelectedLocation] = useState(null);

  const handleMapClick = (e) => {
    const { lat, lng } = e.latlng;
    setSelectedLocation({ lat, lng });
    if (onLocationSelect) {
      onLocationSelect({ lat, lng });
    }
  };

  return (
    <MapContainer
      center={[48.1351, 11.5820]} // München
      zoom={13}
      style={{ height: "100%", width: "100%" }}
      zoomControl={false} // Deaktiviere Standard-Zoom-Control
      onClick={handleMapClick}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <ZoomControl position="topright" />
      <LocationMarker />
      <MapControls />

      {selectedLocation && (
        <Marker position={[selectedLocation.lat, selectedLocation.lng]}>
          <Popup>
            <div className="selected-location-popup">
              <h3>Ausgewählter Standort</h3>
              <p>Lat: {selectedLocation.lat.toFixed(4)}</p>
              <p>Lng: {selectedLocation.lng.toFixed(4)}</p>
            </div>
          </Popup>
        </Marker>
      )}
    </MapContainer>
  );
};

export default Map;
