import React, { useState } from 'react';
import '../styles/MapPage.css';
import Map from '../components/Map';
import { MapContainer, TileLayer } from 'react-leaflet';

const MapPage = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [showTransportDropdown, setShowTransportDropdown] = useState(false);
  const [selectedRoute, setSelectedRoute] = useState(null);

  const transportOptions = [
    { id: 'car', label: '🚗 Auto' },
    { id: 'carsharing', label: '🚙 Carsharing' },
    { id: 'uber', label: '🚖 Uber/Taxi' },
    { id: 'bike', label: '🚲 Fahrrad' },
    { id: 'ebike', label: '⚡ E-Bike' },
    { id: 'scooter', label: '🛴 E-Scooter' },
    { id: 'bus', label: '🚌 Bus' },
    { id: 'train', label: '🚂 Bahn' },
    { id: 'walk', label: '🚶 Zu Fuß' }
  ];

  const routeOptions = [
    {
      id: 1,
      icon: '🚗',
      title: 'Mit dem Auto',
      duration: '25 min',
      distance: '12.5 km',
      cost: '4.50 €',
      co2: '2.8 kg',
      badge: 'fastest'
    },
    {
      id: 2,
      icon: '🚲',
      title: 'Mit dem Fahrrad',
      duration: '45 min',
      distance: '11.8 km',
      cost: '0 €',
      co2: '0 kg',
      badge: 'eco'
    },
    {
      id: 3,
      icon: '🚌',
      title: 'Mit Bus & Bahn',
      duration: '35 min',
      distance: '13.2 km',
      cost: '2.90 €',
      co2: '0.5 kg',
      badge: 'cheapest'
    }
  ];

  const getBadgeLabel = (badge) => {
    switch (badge) {
      case 'fastest': return 'Schnellste Route';
      case 'cheapest': return 'Günstigste Route';
      case 'eco': return 'Umweltfreundlichste';
      default: return '';
    }
  };

  const handleRouteCalculation = () => {
    // TODO: Implement actual route calculation
    console.log('Calculating route from', startLocation, 'to', endLocation);
  };

  return (
    <div className="map-container">
      <div className={`sidebar ${sidebarCollapsed ? 'collapsed' : ''}`}>
        <button 
          className="sidebar-toggle"
          onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
        >
          {sidebarCollapsed ? '→' : '←'}
        </button>

        <div className="search-container">
          <div className="search-box">
            <div className="search-input-group">
              <i className="fas fa-map-marker-alt"></i>
              <input
                type="text"
                className="search-input"
                placeholder="Startpunkt eingeben..."
                value={startLocation}
                onChange={(e) => setStartLocation(e.target.value)}
              />
            </div>

            <div className="search-input-group">
              <i className="fas fa-flag-checkered"></i>
              <input
                type="text"
                className="search-input"
                placeholder="Zielpunkt eingeben..."
                value={endLocation}
                onChange={(e) => setEndLocation(e.target.value)}
              />
            </div>

            <div className="transport-dropdown">
              <button 
                className="dropdown-button"
                onClick={() => setShowTransportDropdown(!showTransportDropdown)}
              >
                <span>Weitere Transportmittel</span>
                <i className={`fas fa-chevron-${showTransportDropdown ? 'up' : 'down'}`}></i>
              </button>

              {showTransportDropdown && (
                <div className="dropdown-content">
                  {transportOptions.map(option => (
                    <div 
                      key={option.id}
                      className="dropdown-item"
                      onClick={() => {
                        setActiveFilters(prev => 
                          prev.includes(option.id)
                            ? prev.filter(id => id !== option.id)
                            : [...prev, option.id]
                        );
                      }}
                    >
                      {option.label}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <button 
              className="button button-primary calculate-route-btn"
              onClick={handleRouteCalculation}
            >
              <i className="fas fa-route"></i>
              Route berechnen
            </button>
          </div>

          <div className="route-options">
            {routeOptions.map(route => (
              <div
                key={route.id}
                className={`route-option ${selectedRoute === route.id ? 'selected' : ''}`}
                onClick={() => setSelectedRoute(route.id)}
              >
                <div className="route-option-icon">
                  {route.icon}
                </div>
                <div className="route-option-info">
                  <div className="route-option-title">
                    {route.title}
                    {route.badge && (
                      <span className={`route-option-badge badge-${route.badge}`}>
                        {getBadgeLabel(route.badge)}
                      </span>
                    )}
                  </div>
                  <div className="route-option-details">
                    <span>{route.duration}</span>
                    <span>{route.distance}</span>
                    <span>{route.cost}</span>
                    <span>CO₂: {route.co2}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="map-view">
        <MapContainer
          center={[48.1351, 11.5820]}
          zoom={13}
          style={{ height: "600px", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
        </MapContainer>
      </div>

      <div className="route-info card">
        <h3>Routeninformation</h3>
        <div className="route-stats">
          <div className="stat">
            <i className="fas fa-clock"></i>
            <span>25 min</span>
          </div>
          <div className="stat">
            <i className="fas fa-road"></i>
            <span>5.2 km</span>
          </div>
          <div className="stat">
            <i className="fas fa-leaf"></i>
            <span>-2.3 kg CO₂</span>
          </div>
        </div>
        <div className="route-details">
          <h4>Details</h4>
          <ul>
            <li><i className="fas fa-arrow-right"></i> Start: Bahnhofstraße</li>
            <li><i className="fas fa-arrow-right"></i> Über: Hauptstraße</li>
            <li><i className="fas fa-arrow-right"></i> Ziel: Marktplatz</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default MapPage;
