import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import '../styles/MapPage.css';
import Map from '../components/Map';

const MapPage = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [activeFilters, setActiveFilters] = useState([]);
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [selectedFilters, setSelectedFilters] = useState({
    bike: true,
    walk: true,
    publicTransport: true,
    environment: false
  });

  const filterCategories = [
    {
      title: 'Transportmittel',
      options: [
        { id: 'bike', label: '🚲 Fahrrad' },
        { id: 'bus', label: '🚌 Bus' },
        { id: 'train', label: '🚂 Bahn' },
        { id: 'walk', label: '🚶 Zu Fuß' }
      ]
    },
    {
      title: 'Orte',
      options: [
        { id: 'park', label: '🌳 Parks' },
        { id: 'restaurant', label: '🍽️ Restaurants' },
        { id: 'shop', label: '🛍️ Geschäfte' },
        { id: 'culture', label: '🎭 Kultur' }
      ]
    }
  ];

  const toggleFilter = (filterId) => {
    setActiveFilters(prev => 
      prev.includes(filterId)
        ? prev.filter(id => id !== filterId)
        : [...prev, filterId]
    );
  };

  const handleRouteCalculation = () => {
    // TODO: Implement route calculation
    console.log('Calculating route from', startLocation, 'to', endLocation);
  };

  const toggleFilterOld = (filter) => {
    setSelectedFilters(prev => ({
      ...prev,
      [filter]: !prev[filter]
    }));
  };

  return (
    <div className="map-container">
      <div className={`sidebar ${sidebarCollapsed ? 'collapsed' : ''}`}>
        <button 
          className="sidebar-toggle"
          onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
        >
          {sidebarCollapsed ? '→' : '←'}
        </button>

        {filterCategories.map(category => (
          <div key={category.title} className="filter-section">
            <h3>{category.title}</h3>
            <div className="filter-options">
              {category.options.map(option => (
                <div
                  key={option.id}
                  className={`filter-option ${activeFilters.includes(option.id) ? 'active' : ''}`}
                  onClick={() => toggleFilter(option.id)}
                >
                  {option.label}
                </div>
              ))}
            </div>
          </div>
        ))}

        <div className="search-container">
          <div className="search-box">
            <div className="search-input-group">
              <i className="fas fa-map-marker-alt start-icon"></i>
              <input
                type="text"
                className="search-input"
                placeholder="Startpunkt eingeben..."
                value={startLocation}
                onChange={(e) => setStartLocation(e.target.value)}
              />
            </div>
            <div className="search-input-group">
              <i className="fas fa-flag-checkered end-icon"></i>
              <input
                type="text"
                className="search-input"
                placeholder="Zielpunkt eingeben..."
                value={endLocation}
                onChange={(e) => setEndLocation(e.target.value)}
              />
            </div>
            <button 
              className="button button-primary calculate-route-btn"
              onClick={handleRouteCalculation}
            >
              <i className="fas fa-route"></i>
              Route berechnen
            </button>
          </div>

          <div className="filters">
            <button 
              className={`filter-btn ${selectedFilters.bike ? 'active' : ''}`}
              onClick={() => toggleFilterOld('bike')}
            >
              <i className="fas fa-bicycle"></i>
              Fahrrad
            </button>
            <button 
              className={`filter-btn ${selectedFilters.walk ? 'active' : ''}`}
              onClick={() => toggleFilterOld('walk')}
            >
              <i className="fas fa-walking"></i>
              Zu Fuß
            </button>
            <button 
              className={`filter-btn ${selectedFilters.publicTransport ? 'active' : ''}`}
              onClick={() => toggleFilterOld('publicTransport')}
            >
              <i className="fas fa-bus"></i>
              ÖPNV
            </button>
            <button 
              className={`filter-btn ${selectedFilters.environment ? 'active' : ''}`}
              onClick={() => toggleFilterOld('environment')}
            >
              <i className="fas fa-leaf"></i>
              Umweltdaten
            </button>
          </div>
        </div>
      </div>

      <div className="map-view">
        <MapContainer
          center={[48.1351, 11.5820]}
          zoom={13}
          style={{ height: "600px", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          <LocationMarker />
        </MapContainer>
      </div>

      <div className="route-info card">
        <h3>Routeninformation</h3>
        <div className="route-stats">
          <div className="stat">
            <i className="fas fa-clock"></i>
            <span>25 min</span>
          </div>
          <div className="stat">
            <i className="fas fa-road"></i>
            <span>5.2 km</span>
          </div>
          <div className="stat">
            <i className="fas fa-leaf"></i>
            <span>-2.3 kg CO₂</span>
          </div>
        </div>
        <div className="route-details">
          <h4>Details</h4>
          <ul>
            <li><i className="fas fa-arrow-right"></i> Start: Bahnhofstraße</li>
            <li><i className="fas fa-arrow-right"></i> Über: Hauptstraße</li>
            <li><i className="fas fa-arrow-right"></i> Ziel: Marktplatz</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default MapPage;
