import React, { useState } from 'react';
import '../styles/Explorer.css';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const places = [
  { id: 1, name: 'Englischer Garten', category: 'park', rating: 4.8, image: '/assets/places/park.jpg', description: 'Ein großer öffentlicher Park in München.' },
  { id: 2, name: 'Deutsches Museum', category: 'museum', rating: 4.7, image: '/assets/places/museum.jpg', description: 'Das größte Wissenschafts- und Technikmuseum der Welt.' },
  { id: 3, name: 'Viktualienmarkt', category: 'market', rating: 4.6, image: '/assets/places/market.jpg', description: 'Ein täglicher Lebensmittelmarkt und ein Platz in der Altstadt.' },
  { id: 4, name: 'Olympiapark', category: 'park', rating: 4.5, image: '/assets/places/park.jpg', description: 'Ein Veranstaltungsort für kulturelle und soziale Veranstaltungen.' }
];

const events = [
  { id: 1, title: 'Konzert im Park', date: '2024-12-15', time: '18:00', location: 'Englischer Garten' },
  { id: 2, title: 'Museum Nacht', date: '2024-12-18', time: '20:00', location: 'Deutsches Museum' },
  { id: 3, title: 'Marktfest', date: '2024-12-20', time: '10:00', location: 'Viktualienmarkt' },
  { id: 4, title: 'Kunst im Park', date: '2024-12-22', time: '14:00', location: 'Olympiapark' }
];

const groups = [
  { id: 1, name: 'Wanderfreunde München', description: 'Gemeinsam die Natur genießen.', location: [48.1351, 11.582], image: '/assets/groups/hiking.jpg' },
  { id: 2, name: 'Tech Meetups', description: 'Treffen für Technikbegeisterte.', location: [48.1374, 11.5755], image: '/assets/groups/tech.jpg' },
  { id: 3, name: 'Kunstliebhaber', description: 'Kunst und Kultur erleben.', location: [48.1386, 11.576], image: '/assets/groups/art.jpg' },
  { id: 4, name: 'Fotografie-Enthusiasten', description: 'Gemeinsame Fotowalks und Workshops.', location: [48.1402, 11.582], image: '/assets/groups/photo.jpg' }
];

const wishlist = [
  { id: 1, name: 'Bergwandern', category: 'Aktivität', image: '/assets/wishlist/hiking.jpg', description: 'Eine aufregende Bergwanderung in den Alpen.' },
  { id: 2, name: 'Kunstgalerie besuchen', category: 'Aktivität', image: '/assets/wishlist/art.jpg', description: 'Besuch einer modernen Kunstgalerie.' },
  { id: 3, name: 'Kochkurs', category: 'Aktivität', image: '/assets/wishlist/cooking.jpg', description: 'Lernen Sie, authentische bayerische Gerichte zu kochen.' },
  { id: 4, name: 'Buchclub', category: 'Gruppe', image: '/assets/wishlist/book.jpg', description: 'Treten Sie einem Buchclub bei und diskutieren Sie über Literatur.' }
];

function Explorer() {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('popular');
  const [selectedDate, setSelectedDate] = useState(new Date());

  const filteredPlaces = places.filter(place => {
    const matchesCategory = selectedCategory === 'all' || place.category === selectedCategory;
    const matchesSearch = place.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const filteredEvents = events.filter(event => {
    return new Date(event.date).toDateString() === selectedDate.toDateString();
  });

  return (
    <div className="explorer-page">
      <header className="explorer-header">
        <nav className="fixed-nav">
          <ul>
            <li><a href="#places">Orte Entdecken</a></li>
            <li><a href="#activities">Aktivitäten & Events</a></li>
            <li><a href="#groups">Gruppen & Meetups</a></li>
            <li><a href="#wishlist">Wunschliste</a></li>
          </ul>
        </nav>
      </header>
      <div className="explorer-controls">
        <input
          type="text"
          placeholder="Nach Orten suchen..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="all">Alle Kategorien</option>
          <option value="park">Parks</option>
          <option value="museum">Museen</option>
          <option value="market">Märkte</option>
          <option value="landmark">Sehenswürdigkeiten</option>
        </select>
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="popular">Beliebteste Orte</option>
          <option value="new">Neu hinzugefügt</option>
          <option value="nearby">In meiner Nähe</option>
        </select>
      </div>
      <div className="explorer-sections">
        <section id="places" className="section-places">
          <h2>Orte Entdecken</h2>
          <div className="places-grid">
            {filteredPlaces.slice(0, 4).map(place => (
              <div key={place.id} className="place-card">
                <div className="place-image">
                  <img src={place.image} alt={place.name} />
                </div>
                <div className="place-info">
                  <h3>{place.name}</h3>
                  <p className="category">{place.category}</p>
                  <p className="description">{place.description}</p>
                  <div className="rating">
                    <span className="stars">{'★'.repeat(Math.floor(place.rating))}</span>
                    <span className="rating-number">{place.rating}</span>
                  </div>
                  <button className="button-primary">Jetzt erkunden</button>
                </div>
              </div>
            ))}
          </div>
        </section>
        <section id="activities" className="section-activities">
          <h2>Aktivitäten & Events</h2>
          <Calendar onChange={setSelectedDate} value={selectedDate} />
          <div className="events-list">
            {filteredEvents.slice(0, 4).map(event => (
              <div key={event.id} className="event-card">
                <h3>{event.title}</h3>
                <p>Datum: {event.date}</p>
                <p>Zeit: {event.time}</p>
                <p>Ort: {event.location}</p>
                <button className="button-primary">Veranstaltung erstellen</button>
              </div>
            ))}
          </div>
        </section>
        <section id="groups" className="section-groups">
          <h2>Gruppen & Meetups</h2>
          <MapContainer center={[48.1351, 11.582]} zoom={13} style={{ height: '400px', width: '100%' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />
            {groups.map(group => (
              <Marker key={group.id} position={group.location}>
                <Popup>
                  <div className="group-card">
                    <img src={group.image} alt={group.name} style={{ width: '100%' }} />
                    <h3>{group.name}</h3>
                    <p>{group.description}</p>
                    <button className="button-primary">Mitmachen</button>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </section>
        <section id="wishlist" className="section-wishlist">
          <h2>Wunschliste</h2>
          <div className="wishlist-grid">
            {wishlist.slice(0, 4).map(item => (
              <div key={item.id} className="wishlist-card">
                <img src={item.image} alt={item.name} />
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                <button className="button-secondary">Entfernen</button>
              </div>
            ))}
          </div>
        </section>
      </div>
      <footer className="explorer-footer">
        <div className="footer-content">
          <p>Kontaktinformationen</p>
          <p>Datenschutz | Impressum</p>
          <p>Folgen Sie uns auf sozialen Medien</p>
        </div>
      </footer>
    </div>
  );
}

export default Explorer;
