import React, { useState } from 'react';
import '../styles/Explorer.css';
// Ensure react-calendar is installed
// Run the following command in your terminal:
// npm install react-calendar
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const places = [
  { id: 1, name: 'Englischer Garten', category: 'park', rating: 4.8 },
  { id: 2, name: 'Deutsches Museum', category: 'museum', rating: 4.7 },
  { id: 3, name: 'Viktualienmarkt', category: 'market', rating: 4.6 },
  { id: 4, name: 'Olympiapark', category: 'park', rating: 4.5 },
  { id: 5, name: 'Pinakothek der Moderne', category: 'museum', rating: 4.6 },
  { id: 6, name: 'Marienplatz', category: 'landmark', rating: 4.7 }
];

const events = [
  { id: 1, title: 'Konzert im Park', date: '2024-12-15', time: '18:00', location: 'Englischer Garten' },
  { id: 2, title: 'Museum Nacht', date: '2024-12-18', time: '20:00', location: 'Deutsches Museum' },
  { id: 3, title: 'Marktfest', date: '2024-12-20', time: '10:00', location: 'Viktualienmarkt' }
];

const groups = [
  { id: 1, name: 'Wanderfreunde München', description: 'Gemeinsam die Natur genießen.', location: [48.1351, 11.582], image: '/assets/groups/hiking.jpg' },
  { id: 2, name: 'Tech Meetups', description: 'Treffen für Technikbegeisterte.', location: [48.1374, 11.5755], image: '/assets/groups/tech.jpg' },
  { id: 3, name: 'Kunstliebhaber', description: 'Kunst und Kultur erleben.', location: [48.1386, 11.576], image: '/assets/groups/art.jpg' }
];

function Explorer() {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('popular');
  const [selectedDate, setSelectedDate] = useState(new Date());

  const filteredPlaces = places.filter(place => {
    const matchesCategory = selectedCategory === 'all' || place.category === selectedCategory;
    const matchesSearch = place.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  const filteredEvents = events.filter(event => {
    return new Date(event.date).toDateString() === selectedDate.toDateString();
  });

  return (
    <div className="explorer-page">
      <div className="explorer-header">
        <h1>Entdecken Sie interessante Orte</h1>
        <div className="explorer-controls">
          <input
            type="text"
            placeholder="Nach Orten suchen..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="all">Alle Kategorien</option>
            <option value="park">Parks</option>
            <option value="museum">Museen</option>
            <option value="market">Märkte</option>
            <option value="landmark">Sehenswürdigkeiten</option>
          </select>
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          >
            <option value="popular">Beliebteste Orte</option>
            <option value="new">Neu hinzugefügt</option>
            <option value="nearby">In meiner Nähe</option>
          </select>
        </div>
      </div>
      <div className="explorer-sections">
        <section className="section-places">
          <h2>Orte Entdecken</h2>
          <div className="places-grid">
            {filteredPlaces.map(place => (
              <div key={place.id} className="place-card">
                <div className="place-image">
                  <img src={`/assets/places/${place.category}.jpg`} alt={place.name} />
                </div>
                <div className="place-info">
                  <h3>{place.name}</h3>
                  <p className="category">{place.category}</p>
                  <p className="description">Kurzbeschreibung des Ortes...</p>
                  <div className="rating">
                    <span className="stars">{'★'.repeat(Math.floor(place.rating))}</span>
                    <span className="rating-number">{place.rating}</span>
                  </div>
                  <button className="button-primary">Route planen</button>
                </div>
              </div>
            ))}
          </div>
        </section>
        <section className="section-activities">
          <h2>Aktivitäten & Events</h2>
          <Calendar onChange={setSelectedDate} value={selectedDate} />
          <div className="events-list">
            {filteredEvents.map(event => (
              <div key={event.id} className="event-card">
                <h3>{event.title}</h3>
                <p>Datum: {event.date}</p>
                <p>Zeit: {event.time}</p>
                <p>Ort: {event.location}</p>
              </div>
            ))}
          </div>
        </section>
        <section className="section-groups">
          <h2>Gruppen & Meetups</h2>
          <MapContainer center={[48.1351, 11.582]} zoom={13} style={{ height: '400px', width: '100%' }}>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />
            {groups.map(group => (
              <Marker key={group.id} position={group.location}>
                <Popup>
                  <div className="group-card">
                    <img src={group.image} alt={group.name} style={{ width: '100%' }} />
                    <h3>{group.name}</h3>
                    <p>{group.description}</p>
                    <button className="button-primary">Mitmachen</button>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </section>
        <section className="section-wishlist">
          <h2>Wunschliste</h2>
          <p>Speichern Sie Ihre Lieblingsorte auf Ihrer Wunschliste.</p>
        </section>
      </div>
    </div>
  );
}

export default Explorer;
