import React, { useState } from 'react';
import '../styles/MapPage.css';
import Map from '../components/Map';
import MapSidebar from '../components/MapSidebar';
import { MapContainer, TileLayer } from 'react-leaflet';

const MapPage = () => {
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [transportMode, setTransportMode] = useState('');
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [showTransportDropdown, setShowTransportDropdown] = useState(false);

  const transportOptions = [
    { id: 'car', label: '🚗 Auto' },
    { id: 'bike', label: '🚲 Velo' },
    { id: 'train', label: '🚆 Zug' },
    { id: 'bus', label: '🚌 Bus' },
    { id: 'scooter', label: '🛴 E-Trottinett' },
    { id: 'ebike', label: '⚡ E-Bike' },
    { id: 'taxi', label: '🚕 Taxi' },
    { id: 'walk', label: '🚶‍♂️ Zu Fuß' }
  ];

  const routeOptions = [
    {
      id: 1,
      icon: '🚗',
      title: 'Mit dem Auto',
      duration: '20',
      distance: '10.3',
      cost: '3.80',
      co2: '2.3',
      badge: 'fastest'
    },
    {
      id: 2,
      icon: '🚲',
      title: 'Mit dem Velo',
      duration: '35',
      distance: '10.3',
      cost: '0.00',
      co2: '0.00',
      badge: 'eco'
    },
    {
      id: 3,
      icon: '🚌',
      title: 'Mit Bus & Zug',
      duration: '28',
      distance: '11.5',
      cost: '2.70',
      co2: '0.4',
      badge: 'cheapest'
    }
  ];

  const handleRouteCalculation = () => {
    // Implement actual route calculation
    console.log('Calculating route from', startLocation, 'to', endLocation, 'using', transportMode);
  };

  return (
    <div className="map-container">
      <div className="sidebar left-sidebar">
        <div className="search-container">
          <div className="search-box">
            <div className="search-input-group">
              <span role="img" aria-label="start">📍</span>
              <input
                type="text"
                className="search-input"
                placeholder="Startort eingeben..."
                value={startLocation}
                onChange={(e) => setStartLocation(e.target.value)}
              />
            </div>

            <div className="search-input-group">
              <span role="img" aria-label="end">🏁</span>
              <input
                type="text"
                className="search-input"
                placeholder="Zielort eingeben..."
                value={endLocation}
                onChange={(e) => setEndLocation(e.target.value)}
              />
            </div>

            <div className="transport-dropdown">
              <button 
                className="dropdown-button"
                onClick={() => setShowTransportDropdown(!showTransportDropdown)}
              >
                <span>Transportmittel</span>
                <i className={`fas fa-chevron-${showTransportDropdown ? 'up' : 'down'}`}></i>
              </button>

              {showTransportDropdown && (
                <div className="dropdown-content">
                  {transportOptions.map(option => (
                    <div 
                      key={option.id}
                      className={`dropdown-item ${transportMode === option.id ? 'selected' : ''}`}
                      onClick={() => setTransportMode(option.id)}
                    >
                      {option.label}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <button 
              className="button button-primary calculate-route-btn"
              onClick={handleRouteCalculation}
            >
              <i className="fas fa-route"></i>
              Route berechnen
            </button>
          </div>

          <div className="route-options">
            {routeOptions.map(route => (
              <div
                key={route.id}
                className={`route-option ${selectedRoute === route.id ? 'selected' : ''}`}
                onClick={() => setSelectedRoute(route.id)}
              >
                <div className="route-option-icon">
                  {route.icon}
                </div>
                <div className="route-option-info">
                  <div className="route-option-title">
                    {route.title}
                    {route.badge && (
                      <span className={`route-option-badge badge-${route.badge}`}>
                        {route.badge === 'fastest' ? 'Schnellste Route' : route.badge === 'eco' ? 'Umweltfreundlichste Route' : 'Günstigste Route'}
                      </span>
                    )}
                  </div>
                  <div className="route-details">
                    <span>Min. {route.duration}</span>
                    <span>km {route.distance}</span>
                    <span>CHF {route.cost}</span>
                    <span>CO₂ {route.co2}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="map-view">
        <MapContainer
          center={[48.1351, 11.5820]}
          zoom={13}
          style={{ height: "600px", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
        </MapContainer>
      </div>

      <div className="sidebar right-sidebar">
        <div className="route-info card">
          <h3>Routeninformationen</h3>
          {selectedRoute !== null && (
            <div className="route-details">
              <p>Transportmittel: {transportOptions.find(option => option.id === transportMode)?.label}</p>
              <p>Zeit: Min. {routeOptions[selectedRoute - 1].duration}</p>
              <p>Distanz: km {routeOptions[selectedRoute - 1].distance}</p>
              <p>CO₂-Einsparung: CO₂ {routeOptions[selectedRoute - 1].co2}</p>
              <p>Start: Bahnhofstraße 🏁</p>
              <p>Über: Hauptstraße ➡️</p>
              <p>Ziel: Marktplatz 📍</p>
              <p>Wetter: ☀️ Sonnig, 23°C</p>
              <p>Verkehr: Keine Staus ⚠️</p>
              <div className="stops">
                <p>Zwischenstopps:</p>
                <ul>
                  <li>🛑 Cafe Müller</li>
                  <li>🌳 Park Grüner Weg</li>
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MapPage;
